mapper.update:
UPDATE merchandise
SET readcount = readcount + 1
WHERE num = #{num} AND majorc = #{majorc} AND subc = #{subc}

mapper.select:
SELECT a.* 
FROM (SELECT * 
	  FROM merchandise
	  WHERE name = #{name}
	  AND majorc = #{majorc}
	  AND subc = #{subc}
	  ORDER BY price ASC) a

WHERE ROWNUM <= 8

